import stringIsTrue from '../src/stringIsTrue';

/** @test {stringIsTrue} */
describe('stringIsTrue => true', () => {
  it('With string "true"', () => {
    expect(stringIsTrue('true')).toBe(true);
  });

  it('With string "1"', () => {
    expect(stringIsTrue('1')).toBe(true);
  });

  it('With number 1', () => {
    expect(stringIsTrue(1)).toBe(true);
  });

  it('With boolean true', () => {
    expect(stringIsTrue(true)).toBe(true);
  });
});

/** @test {stringIsTrue} */
describe('stringIsTrue => false', () => {
  it('With string "tech"', () => {
    expect(stringIsTrue('tech')).toBe(false);
  });

  it('With number 0', () => {
    expect(stringIsTrue(0)).toBe(false);
  });
});
