import moment from 'moment/moment';

/**
 * Sort any dates and return it as an array
 * @param {Date | import('moment').Moment | String} date1 Date
 * @param {Date | import('moment').Moment | String} date2 Date
 * @param {Date | import('moment').Moment | String} date3 Date
 * @return {Array} Sorted dates
 *
 * @example
 * import sortDateRange from '@meta-awesome/functions/src/sortDateRange'
 *
 * const myDates = [moment('2019-01-02'), moment('2019-01-01'), moment('2019-01-03')]
 * sortDateRange(...myDates)
 * // ['2019-01-01', '2019-01-02', '2019-01-03']
 * */
const sortDateRange = (...dates) => {
  dates = dates.map(date => moment(date));
  dates.sort((left, right) => left.diff(right));
  return dates.map(date => date.format('L'));
};

export default sortDateRange;
