import toString from 'lodash/toString';

/**
 * Check if a string is a "true" value
 * @param {String | Number | Boolean} str Param to comparate
 * @return {Boolean}
 *
 * @example
 * import stringIsTrue from '@meta-awesome/functions/src/stringIsTrue'
 *
 * stringIsTrue('true')
 * // true
 *
 * stringIsTrue('1')
 * // true
 *
 * stringIsTrue(1)
 * //true
 *
 * stringIsTrue(true)
 * // true
 * */
const stringIsTrue = str =>
  str === true || str === toString(true) || str === 1 || str === toString(1);

export default stringIsTrue;
