import flow from 'lodash/fp/flow';
import snakeCase from 'lodash/fp/snakeCase';
import toUpper from 'lodash/fp/toUpper';

/**
 * upperSnakeCase
 * Convert a space separated string to an upper snake cased string
 * @param {String} string
 * @return {String}
 *
 * @example
 * import upperSnakeCase from '@meta-awesome/functions/src/upperSnakeCase'
 *
 * upperSnakeCase('cat and dog'')
 * // CAT_AND_DOG
 * */
const upperSnakeCase = string =>
  flow(
    snakeCase,
    toUpper
  )(string);

export default upperSnakeCase;
